package com.dayone.firstprog;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int value1= 9 / 2 ;  //integer division
		float value2 = 101f / 61f;
		double value3 = 10d/6d;
		System.out.println("value 1="+ value1);
		System.out.println("value 2 this is in float="+ value2);
		System.out.println("value 3 this in double ="+ value3);
		
		
		int marker= 512;
		double percentage = marker * 0.45f;
		System.out.println("percentage :" + percentage );
		
		
		
	}

}
